import React from "react";

export const SizeModal = ({ product }) => {
  return (
    <div className="fixed w-screen h-screen flex items-center justify-center top-0 right-0 bg-[#00000062] z-50">
      <div className="rounded w-1/4 h-96 bg-[#f1f1f1] p-6">
        <div className="flex gap-4">
          <div className="object-cover w-1/5">
            <img src={product.url} className="w-full h-auto" alt="" />
          </div>
          <div className="flex flex-col gap-2">
            <span className="font-titleFont font-semibold">{product.name}</span>
            <span className="font-bodyFont font-light text-gray-500">
              {product.category}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};
