import React, { useContext } from "react";

import CartContext from "../context/cartContext";
import { CartItem } from "../components/CartItem";
export const Cart = () => {
  const { cartArray } = useContext(CartContext);
  // let html;
  // for (let item of cartArray) {
  //   html = <div>{item.name + " " + item.qty}</div>;
  // }
  // console.log(cartArray);
  return (
    <div>
      <div className="w-4/5 flex mx-auto">
        <div className="w-3/5 flex flex-col gap-4 px-6 border-r border-r-gray-300 py-8 ">
          <div className="border py-4 px-3 flex justify-between items-center w-full rounded-sm text-xs font-medium">
            <span>Check delivery time & services</span>
            <span className="border border-green-600 text-green-600 px-3 py-2 uppercase text-xs">
              Enter pin code
            </span>
          </div>
          {cartArray.length ? (
            cartArray.map((item) => <CartItem key={item._id} product={item} />)
          ) : (
            <>
              <div>No Items</div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};
