import React, { useContext } from "react";
import UserContext from "../context/userContext";

export const UserMenu = () => {
  const auth = useContext(UserContext);
  return (
    <div className="box-shadow p-4 bg-white w-full flex flex-col gap-2">
      <span className="font-titleFont font-medium text-lg whitespace-nowrap">
        {auth.user.displayName}
      </span>
      <span className="font-bodyFont text-sm font-light text-gray-400">
        {auth.user.email}
      </span>
    </div>
  );
};
