import React, { useContext, useState } from "react";
import CartContext from "./cartContext";
import UserContext from "./userContext";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../firebase/Firebase";
export const CartProvider = async ({ children }) => {
  // const [selectedCategory, setSelectedCategory] = useState(null);
  const auth = useContext(UserContext);

  const [cartArray, setCartArray] = useState([]);
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState(null);
  if (auth.user) {
    const docRef = doc(db, "users", auth.user.email);
    const data = await getDoc(docRef);
    if (data.exists()) {
      setCartArray(data.data().cart);
    }
  }
  const addToCart = (_id, name, url, category, keywords, oldPrice, price) => {
    return setCartArray((cartArray) => [
      ...cartArray,
      {
        _id: _id,
        name: name,
        url: url,
        category: category,
        keywords: keywords,
        oldPrice: oldPrice,
        price: price,
        qty: qty,
        size: size,
      },
    ]);
  };
  return (
    <CartContext.Provider
      value={{ cartArray, addToCart, qty, setQty, size, setSize, setCartArray }}
    >
      {children}
    </CartContext.Provider>
  );
};
