import React, { useContext } from "react";
import UserContext from "../context/userContext";

export const UserMenu = () => {
  const auth = useContext(UserContext);
  return (
    <div className="box-shadow p-4 bg-white w-max flex flex-col gap-2">
      <div className="flex gap-2 items-center">
        <img src={auth.user.photoURL} className="w-14 rounded-full" alt="" />
        <div className="flex flex-col gap-1">
          <span className="font-titleFont font-medium text-lg whitespace-nowrap">
            {auth.user.displayName}
          </span>
          <span className="font-bodyFont text-sm font-light text-gray-500">
            {auth.user.email}
          </span>
        </div>
      </div>
    </div>
  );
};
