import React from "react";
import { facebook, google, login } from "../assets";

export const Login = () => {
  return (
    <div className="w-3/5 py-20 flex gap-8 mx-auto items-center">
      <div>
        <img src={login} alt="" className="w-[60rem]" />
      </div>
      <div className="flex flex-col gap-8 w-full">
        <div className="flex flex-col gap-1">
          <h1 className="text-3xl font-bold font-bodyFont tracking-wider">
            Welcome Back!
          </h1>
          <span className="font-light text-base text-gray-500">
            Login to continue
          </span>
        </div>
        <div className="flex flex-col gap-6">
          <input
            type="text"
            className="border border-gray-500 px-4 py-2 w-full"
          />
          <input
            type="text"
            className="border border-gray-500 px-4 py-2 w-full"
          />
        </div>
        <div className="flex gap-10 items-center justify-between">
          <button className="w-48 text-base uppercase text-white tracking-wider font-titleFont font-medium py-3 bg-green-500 rounded-full">
            Login
          </button>
          <span className="uppercase font-titleFont tracking-wider text-sm font-medium text-gray-500">
            forgot password?
          </span>
        </div>
        <div className="flex gap-20 items-center">
          <span className="uppercase font-titleFont font-medium text-sm">
            Login with
          </span>
          <div className="flex gap-4">
            <div className="rounded-full bg-gray-100 p-2 w-full">
              <img src={google} className="w-6" alt="" />
            </div>
            <div className="rounded-full bg-gray-100 p-2 w-full">
              <img src={facebook} className="w-6" alt="" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
