import React from "react";
import { useLocation } from "react-router-dom";

const ProductPage = () => {
  const location = useLocation();
  const product = location.state;
  return (
    <div className="flex gap-10 mx-auto my-10 w-11/12">
      <div className="w-2/5 relative">
        <img
          src={product.url}
          className="w-full h-[550px] object-cover"
          alt=""
        />
        {product.discount > 0 && (
          <span className="absolute top-4 py-2 pr-4 text-xl drop-shadow pl-6 bg-green-700 text-white right-0">
            {product.discount}% off
          </span>
        )}
      </div>
      <div className="w-3/5 flex flex-col justify-center">
        <h2 className="text-5xl font-titleFont font-bold ">{product.name}</h2>
        <div className="flex  gap-4 items-center">
          {product.discount > 0 && (
            <span className="text-gray-700 text-sm line-through ">
              Rs. {product.oldprice}
            </span>
          )}
          <span className="text-xl font-semibold">Rs. {product.price}</span>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
