import React, { useContext } from "react";
import FavContext from "../context/favContext";
import { FavItem } from "../components/FavItem";

export const Favorites = () => {
  const fav = useContext(FavContext);
  return (
    <div className="mx-auto w-5/6 flex flex-col gap-8 py-8">
      <h1 className="text-xl font-semibold font-titleFont">
        My Favorites{" "}
        <span className="font-normal text-gray-700 text-base font-bodyFont">
          ({fav.favArray.length}{" "}
          {fav.favArray.length === 1 ? <span>item</span> : <span>items</span>})
        </span>
      </h1>
      <div className="max-w-screen-xl mx-auto grid mobile:grid-cols-2 tablets:grid-cols-3 xl:grid-cols-4 gap-10 py-10 lg:py-20">
        {fav.favArray.map((item) => (
          <FavItem
            key={item._id + "-" + item.name + "-" + item.category}
            product={item}
          />
        ))}
      </div>
    </div>
  );
};
