import React, { useContext } from "react";
import UserContext from "../context/userContext";
import { getAuth, signOut } from "firebase/auth";

export const UserMenu = () => {
  const auth = useContext(UserContext);
  const handleSignout = () => {
    signOut(getAuth())
      .then(() => {
        console.log(auth);
      })
      .catch((error) => {
        // An error happened.
      });
  };
  return (
    <div className=" p-4 drop-shadow-lg mt-2  bg-white w-max flex flex-col gap-2">
      <div className="flex gap-2 items-center border-b pb-4">
        <img src={auth.user.photoURL} className="w-14 rounded-full" alt="" />
        <div className="flex flex-col gap-0">
          <span className="font-titleFont font-light text-lg whitespace-nowrap">
            Hello,{" "}
            <span className="font-medium">
              {auth.user.displayName.split(" ")[0]}
            </span>
          </span>
          <span className="font-bodyFont text-sm font-light text-gray-500">
            {auth.user.email}
          </span>
        </div>
      </div>
      <div className="border-b pb-2">
        <div className=" p-2 font-light text-sm hover:bg-gray-100 duration-200">
          Favorites
        </div>
      </div>
      <div>
        <div
          onClick={handleSignout}
          className=" p-2 font-light text-sm text-red-500 hover:bg-gray-100 duration-200"
        >
          Sign Out
        </div>
      </div>
    </div>
  );
};
