import React, { useContext } from "react";
import UserContext from "../context/userContext";
import { logout } from "../firebase/Firebase";
import { Link } from "react-router-dom";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth } from "../firebase/Firebase";
export const UserMenu = () => {
  const auth = useContext(UserContext);
  const [user, loading, error] = useAuthState(auth);

  return (
    <div className=" p-4 drop-shadow-lg mt-2  bg-white w-max flex flex-col gap-2">
      <div className="flex gap-2 items-center border-b pb-4">
        <img
          // src={user.name.charAt(0).toUpperCase()}
          className="w-14 rounded-full"
          alt=""
        />
        <div className="flex flex-col gap-0">
          <span className="font-titleFont font-light text-lg whitespace-nowrap">
            Hello,{" "}
            <span className="font-medium">
              {/* {user.displayName.split(" ")[0]} */}
            </span>
          </span>
          <span className="font-bodyFont text-sm font-light text-gray-500">
            {/* {auth.user.email} */}
          </span>
        </div>
      </div>
      <div className="border-b pb-2">
        <Link to="/favorites">
          <div className=" cursor-pointer p-2 font-light text-sm hover:bg-gray-100 duration-200">
            Favorites
          </div>
        </Link>
      </div>
      <div>
        <div
          onClick={logout}
          className=" p-2 cursor-pointer font-light text-sm text-red-500 hover:bg-gray-100 duration-200"
        >
          Sign Out
        </div>
      </div>
    </div>
  );
};
