import React, { useContext } from "react";
import { Link } from "react-router-dom";
import CartContext from "../context/cartContext";
import { CartItem } from "../components/CartItem";
import { blurredProduct } from "../assets";
export const Cart = () => {
  const { cartArray } = useContext(CartContext);
  // let html;
  // for (let item of cartArray) {
  //   html = <div>{item.name + " " + item.qty}</div>;
  // }
  // console.log(cartArray);
  return (
    <div>
      <div className="w-4/5 flex mx-auto">
        <div className="w-3/5 flex flex-col gap-4 px-6 border-r border-r-gray-300 py-8 ">
          <div className="border py-4 px-3 flex justify-between items-center w-full rounded-sm text-xs font-medium">
            <span>Check delivery time & services</span>
            <span className="border border-green-600 text-green-600 px-3 py-2 uppercase text-xs  hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
              Enter pin code
            </span>
          </div>
          {cartArray.length ? (
            cartArray.map((item) => <CartItem key={item._id} product={item} />)
          ) : (
            <>
              <div className="mx-auto">No Items in Cart</div>
              <Link to="/" className="mx-auto mb-4">
                <span className=" mx-auto border border-green-600 text-green-600 px-3 py-2 uppercase text-xs  hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
                  Add Items to Cart
                </span>
              </Link>
            </>
          )}
          <div className="border py-4 px-3 flex justify-between items-center w-full rounded-sm text-xs font-medium">
            <span className="flex gap-4 items-center">
              <img src={blurredProduct} className="w-20" alt="" />
              <span>Login to see items from your existing bag.</span>
            </span>
            <span className="border border-green-600 text-green-600 px-3 py-2 uppercase text-xs hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
              login now
            </span>
          </div>
        </div>
        <div className="py-8 flex flex-col gap-4 px-6">
          <h6 className="uppercase text-sm font-semibold font-titleFont tracking-wider">
            Price Details
            <span className="capitalize font-bodyFont font-medium text-xs pl-2 text-gray-700">
              ({cartArray.length} items)
            </span>
          </h6>
        </div>
      </div>
    </div>
  );
};
