import React, { useContext } from "react";
import { Link } from "react-router-dom";
import CartContext from "../context/cartContext";
import { CartItem } from "../components/CartItem";
import { blurredProduct } from "../assets";
import { CartPrice } from "../components/CartPrice";
import emptyCart from "../assets/empty-cart.png";
export const Cart = () => {
  const { cartArray } = useContext(CartContext);
  // let html;
  // for (let item of cartArray) {
  //   html = <div>{item.name + " " + item.qty}</div>;
  // }
  // console.log(cartArray);

  return (
    <div>
      {cartArray.length ? (
        <div className="w-4/5 flex mx-auto">
          <div className="w-2/3 flex flex-col gap-4 px-6 border-r border-r-gray-300 py-8 ">
            <div className="border py-4 px-3 flex justify-between items-center w-full rounded-sm text-xs font-medium">
              <span>Check delivery time & services</span>
              <span className="border border-green-600 text-green-600 px-3 py-2 uppercase text-xs  hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
                Enter pin code
              </span>
            </div>
            {cartArray.length ? (
              cartArray.map((item) => (
                <CartItem key={item._id} product={item} />
              ))
            ) : (
              <>
                <div className="mx-auto">No Items in Cart</div>
                <Link to="/" className="mx-auto mb-4">
                  <span className=" mx-auto border border-green-600 text-green-600 px-3 py-2 uppercase text-xs  hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
                    Add Items to Cart
                  </span>
                </Link>
              </>
            )}
            <div className="border py-4 px-3 flex justify-between items-center w-full rounded-sm text-xs font-medium">
              <span className="flex gap-4 items-center">
                <img src={blurredProduct} className="w-20" alt="" />
                <span>Login to see items from your existing bag.</span>
              </span>
              <span className="border border-green-600 text-green-600 px-3 py-2 uppercase text-xs hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
                login now
              </span>
            </div>
          </div>
          <div className="py-8 flex flex-col gap-4 px-6 w-1/3">
            <CartPrice />
          </div>
        </div>
      ) : (
        <div className="flex items-center justify-center flex-col gap-4 py-8">
          <img src={emptyCart} alt="" className="w-1/4 mx-auto" />
          <div className="text-center flex flex-col items-center justify-center gap-1 pb-6">
            <div>Hey, it feels so light!</div>
            <div className="font-light text-gray-500 text-xs">
              There is nothing in your cart. Let's add some items.
            </div>
          </div>
          <Link to="/" className="mx-auto mb-4">
            <span className=" mx-auto border border-green-600 text-green-600 px-3 py-2 uppercase text-xs  hover:bg-green-100 duration-200 cursor-pointer rounded-sm">
              Add Items to Cart
            </span>
          </Link>
        </div>
      )}
    </div>
  );
};
