import React from "react";
import { discountCalc } from "../api/List";

export const CartItem = ({ product }) => {
  return (
    <div>
      <div className="border rounded-md p-3 pb-4 flex w-full gap-4">
        <div className="object-contain w-1/5">
          <img
            src={product.url}
            className="w-full h-auto object-contain"
            alt=""
          />
        </div>
        <div className="flex gap-2 text-sm pt-2">
          <h6 className="font-semibold font-titleFont text-base">
            {product.name}
          </h6>
          <div className="flex gap-2  ">
            <span>&#8377;{product.price}</span>
            {product.oldPrice && (
              <>
                <span className="font-light text-xs text-gray-500">
                  MRP{" "}
                  <span className="line-through">
                    &#8377;{product.oldPrice}
                  </span>
                </span>
                <span className="text-green-700 text-xs font-medium">
                  ({discountCalc(product.oldPrice, product.price)}% OFF)
                </span>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
