import React from "react";
import { useLocation } from "react-router-dom";
import { MdOutlineStar } from "react-icons/md";
import { discountCalc } from "../api/List";
const ProductPage = () => {
  const location = useLocation();
  const product = location.state;
  return (
    <div className="flex gap-10 mx-auto my-10 w-11/12">
      <div className="w-2/5 relative">
        <img
          src={product.url}
          className="w-full h-[550px] object-contain"
          alt=""
        />
        {product.oldPrice && (
          <span className="absolute top-4 py-2 pr-4 text-xl drop-shadow pl-6 bg-green-700 text-white right-0">
            {discountCalc(product.oldPrice, product.price)}% off
          </span>
        )}
      </div>
      <div className="w-3/5 gap-4 flex flex-col justify-center">
        <h2 className="text-5xl font-titleFont font-bold ">{product.name}</h2>
        <h6 className="text-xl font-light">
          {product.category.charAt(0).toUpperCase() + product.category.slice(1)}
        </h6>
        <div className="flex  gap-2 items-center mt-3">
          {product.oldPrice && (
            <span className="text-gray-700 text-sm font-base line-through ">
              Rs. {product.oldPrice}
            </span>
          )}
          <span className="text-2xl font-medium">Rs. {product.price}</span>
        </div>
        <div className="flex gap-2 items-center">
          <div className="flex">
            <MdOutlineStar />
            <MdOutlineStar />
            <MdOutlineStar />
            <MdOutlineStar />
            <MdOutlineStar />
          </div>
          <p className="text-xs text-gray-500">(1 Customer Review)</p>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
