import React from "react";
import { google, login } from "../assets";

export const Login = () => {
  return (
    <div className="w-fit flex gap-4 mx-auto items-center">
      <div className="flex flex-col gap-2">
        <img src={login} alt="" className="w-96" />
      </div>
      <div>
        <h1 className="text-3xl font-bold font-titleFont tracking-wider">
          Welcome Back !
        </h1>
        <span className="font-light text-base text-gray-500">
          Login to continue
        </span>
        <div className="flex flex-col gap-4">
          <input type="text" className="border border-gray-500" />
          <input type="text" />
